<?php include("session.php"); /*Check for session is set or not if not redirect to login page */include_once("config.php"); /* Connection String*/include('get-user.php'); /* Geting logged in user details*/include('functions.php');$id=$_GET["id"];$rslt = mysqli_query($con,"SELECT * FROM tbl_brochures WHERE id='".$id."'");$row			= mysqli_fetch_assoc($rslt);$title=$row["title"];$category=$row["category"];$activeStatus=$row["activeStatus"];$brochure=$row["brochure"];$image=$row["image"];// save product detailsif(isset($_POST['submit'])){	$title	= mysqli_real_escape_string($con,$_POST["title"]);	$category		= mysqli_real_escape_string($con,$_POST["category"]);	$activeStatus			= mysqli_real_escape_string($con,$_POST["activeStatus"]);	$path_to_image_directory="../uploads/catalogs/";	if(isset($_FILES['brochure']) && !empty($_FILES['brochure']['name'])) 	{     				if(preg_match('/[.](pdf)|(PDF)|(doc)|(docx)$/', $_FILES['brochure']['name'])) 		{ 			$brochure=upload_image($path_to_image_directory,$_FILES['brochure']['name'],$_FILES['brochure']['tmp_name']);			}	}	$main_image="";	$image = new UploadImage\Image($_FILES);	$image->setLocation("../uploads/images/brochure");	if($image["main_image"]){		$upload = $image->upload(); 		if($upload){			$main_image=$image->getName().".".$image->getMime();					}else{			echo $image["error"]; 		}	}	//insert to tbl_brochures	mysqli_query($con,"update tbl_brochures set title='$title', category='$category',  image='$main_image', brochure='$brochure', activeStatus='$activeStatus', updatedOn=NOW() where id='$id'") or die(mysqli_error($con));	if($_POST["submit"]=="submit-close")	{		echo "<script>location.href = 'manage-brochure.php'</script>";	}	else	{		echo "<script>location.href = 'edit-brochure.php?id=$id'</script>";	}}?><!DOCTYPE html><html lang="en"><head><meta http-equiv="Content-Type" content="text/html; charset=UTF-8" /><meta name="viewport" content="width=device-width, initial-scale=1.0, 