<?php include("session.php"); /*Check for session is set or not if not redirect to login page */include("config.php"); /* Connection String*/include('get-user.php'); /* Geting logged in user details*/if($userType!=1) /* This page only for admin - if normal user redirect to index page */{	header('location: index.php');}error_reporting(0);$editId	= $_GET["id"];$imageHeight	= 180;$imageWidth		= 180;//select category to be editted$rslt=mysqli_query($con,"select * from tbl_insurance where id='$editId'");if(mysqli_num_rows($rslt) == 0){	header('location: manage-insurance.php');}$row=mysqli_fetch_assoc($rslt);$insuranceName	= $row["insurance"];$url		= $row["url"];$logo		= $row["logo"];$details		= $row["profile"];$altTag			= $row["altTag"];$activeStatus	= $row["activeStatus"];// new user submitif(isset($_POST["submit"])){	$insuranceName 	= mysqli_real_escape_string($con,$_POST["insuranceName"]);	$url	= mysqli_real_escape_string($con,$_POST["url"]);	$altTag	= mysqli_real_escape_string($con,$_POST["altTag"]);	$activeStatus=mysqli_real_escape_string($con,$_POST["activeStatus"]);        $profile=mysqli_real_escape_string($con,$_POST["details"]);	// Check whether the email already registered 	$rslt=mysqli_query($con,"select * from tbl_insurance where insurance='$insuranceName' and id!='".$editId."'");	if(mysqli_num_rows($rslt)>0)	{/* email already registered */		$_SESSION["response"]="insurance already created";	}	else	{	if(isset($_FILES['logo']) && !empty($_FILES['logo']['name'])) 	{     		$path_to_image_directory = '../uploads/images/insurance/';		if(preg_match('/[.](jpg)|(gif)|(png)|(JPG)|(GIF)|(PNG)$/', $_FILES['logo']['name'])) 		{			$image_info = getimagesize($_FILES["logo"]["tmp_name"]);			$image_width = $image_info[0];			$image_height = $image_info[1];			if(1)			{				$path = $_FILES["logo"]['name'];				$ext = pathinfo($path, PATHINFO_EXTENSION);						$source = $_FILES["logo"]['tmp_name'];				if(empty($logo))				{  				$logo = "insurance-"; //Image name				// Make sure the fileName is unique				$count = 1;				while (file_exists($path_to_image_directory.$logo.$count.".".$ext))				{					$count++;					}				$logo = $logo . $count.".".$ext;				}				$target = $path_to_image_directory . $logo;				if(!file_exists($path_to_image_directory)) 				{					if(!mkdir($path_to_image_directory, 0777, true)) 					{						die("There was a problem. Please try again!");					}				}        				move_uploaded_file($source, $target);			}			else			{				echo "<script>alert('Dimension not matching. Image will not be uploaded');</script>";			}		}	}		mysqli_query($con,"update tbl_insurance set insurance='".$insuranceName."',altTag='".$altTag."',url='".$url."',logo='".$logo."',activeStatus='".$activeStatus."',updatedOn=NOW(),profile='".$profile."' where id='".$editId."'") or die(mysqli_error($con));		$_SESSION["response"]="insurance Updated";		echo "<script>location.href = 'manage-insurance.php'</script>";	}}if(isset($_POST["deleteImage"])){	unlink("../images/insurance/".$logo);	mysqli_query($con,"update tbl_insurance set logo='' where id='".$editId."'") or die(mysqli_error($con));	echo "<script>location.href = 'edit-insurance.php?id=".$editId."'</script>;";																			}if(isset($_POST["savenetwork"])){	//$_SESSION["tab"] = "networking";	$outPatient=$_POST["outPatient"];        $inPatient=$_POST["inPatient"];	$network=$_POST["network"];	mysqli_query($con,"insert into tbl_insurance_status (outPatient,inPatient,networkId,insuranceId) values('".$outPatient."','".$inPatient."','".$network."','".$editId."')");}if(isset($_POST["updatenetwork"])){	//$_SESSION["tab"] = "networking";	$id=$_POST["updatenetwork"];	$network=$_POST["network".$id];	$inPatient=$_POST["inPatient".$id];        $outPatient=$_POST["outPatient".$id];	mysqli_query($con,"update tbl_insurance_status set network='".$network."',inPatient='".$inPatient."',outPatient='".$outPatient."' where id='".$id."'");}//remove option if(isset($_POST["removenetwork"])){	//$_SESSION["tab"] = "networking";	mysqli_query($con,"delete from tbl_insurance_status where id='".$_POST["removenetwork"]."'");}?><!DOCTYPE html><html lang="en"><head><meta http-equiv="Content-Type" content="text/html; charset=UTF-8" /><meta name="viewport" content="width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=0" /><title><?php echo $title; ?></title><link href="css/main.css" rel="stylesheet" type="text/css" /><!--[if IE 8]><link href="css/ie8.css" rel="stylesheet" type="text/css" /><![endif]--><link href='css/gfonts.css' rel='stylesheet' type='text/css'><?php include_once('js-scripts.php'); ?><script>function showUser(str) {  if (str=="") {    document.getElementById("txtHint").innerHTML="";    return;  }   if (window.XMLHttpRequest) {    // code for IE7+, Firefox, Chrome, Opera, Safari    xmlhttp=new XMLHttpRequest();  } else { // code for IE6, IE5    xmlhttp=new ActiveXObject("Microsoft.XMLHTTP");  }  xmlhttp.onreadystatechange=function() {    if (this.readyState==4 && this.status==200) {      document.getElementById("txtHint").innerHTML=this.responseText;    }  }  xmlhttp.open("GET","getnetwork.php?q="+str,true);  xmlhttp.send();}</script></head><body>	<!-- Fixed top -->	<?php include_once('top-bar.php'); ?>	<!-- /fixed top -->	<!-- Content container -->	<div id="container">		<!-- Sidebar -->		<?php include_once('side-bar.php'); ?>		<!-- /sidebar -->		<!-- Content -->		<div id="content">		    <!-- Content wrapper -->		    <div class="wrapper">			    <!-- Breadcrumbs line -->			    <?php include_once('bread-crumbs.php'); ?>			    <!-- /breadcrumbs line -->			   <br>			    <!-- /page header -->				<h5 class="widget-name"><i class="icon-user-md"></i>Edit insurance <a href="manage-insurance.php" style="float:right;color:#555 !important;"><i style="padding:4px;" class="icon-arrow-left"></i>Back</a></h5>				<form id="va